        html,
body {
  height: 100%;
}


.containerintro{
    width: 100%;
  max-width: 90%;
  height:80%;
  padding: 25px;
  margin: 0 auto;
  background-image: url('https://lordicon.com/icons/wired/outline/478-computer-display.gif');
  background-size:20%;
background-repeat: no-repeat;

}
.containerbanner{
   
  background-image: url('data:image/jpeg;base64,');
  background-size:cover;
background-repeat: no-repeat;
padding: 20px;
      text-align: center;
      border-radius: 5px;
      margin-top: 20px;


}
.containerbanner img {
      max-width: 100px; /* Adjust the logo size */
      margin-bottom: 10px;
    }
.form-signin {
  width: 100%;
  max-width: 40%;
  
  padding: 25px;
  margin: 0 auto;

}
.form-signin .checkbox {
  font-weight: 400;
}
.form-signin .form-control {
  position: relative;
  box-sizing: border-box;
  height: auto;
  padding: 10px;
  font-size: 16px;
}
.form-signin .form-control:focus {
  z-index: 2;
}
.form-signin input[type="email"] {
  margin-bottom: -1px;
  border-bottom-right-radius: 0;
  border-bottom-left-radius: 0;
}
.form-signin input[type="password"] {
  margin-bottom: 10px;
  border-top-left-radius: 0;
  border-top-right-radius: 0;
}

